<script>
// @ is an alias to /src
import HelloWorld from '@/components/HelloWorld.vue'
import axios from 'axios'

export default {
  name: 'HomeView',
  components: {
    HelloWorld
  },
  data() {
    return {
      text: '',
      balance: '',
      urls: [],
      error: '',
      color: [],
      font: [],
      name: '',
      tagline: '',
      industry: '',
      level: 1
    }
  },
  mounted() {
    this.get_balance()
    this.get_ImageDetails()
    this.get_AddDetails()
    this.get_Mimic()
    this.get_Parameters()
    this.get_Sizes()
  },
  methods: {
    async get_balance(id) {
      await axios
        .get(`/charge`)
        .then(response => response.data)
        .then(response => {
          this.balance = response
        })

    },
    async get_image(id) {

      await axios
        .get(`/imagine-result/${id}`)
        .then(response => response.data)
        .then(response => {
          if (!response.imageUrl) {
            setTimeout(() => {
              this.get_image(id)
            }, 2000);
          }
          else {
          }
        })

    },
    async submit() {
      this.error = ''
      document.getElementById('submit').disabled = true
      document.getElementById('submit').innerHTML = 'Building...'
      var text
      if (this.balance === 0) {
        this.$swal(`<div class="swal2-icon swal2-error swal2-icon-show" style="display: flex;"><span class="swal2-x-mark"><span class="swal2-x-mark-line-left"></span><span class="swal2-x-mark-line-right"></span></span></div><h5>Please Charge Your Account First</h5>`)
        setTimeout(() => {
          const toPath = this.$route.go || '/charge'
          this.$router.push(toPath)
        }, 2000);
        return false
      }
      text = `Create an image-based logo for “${this.name}”, in "${this.industry}"`
      if (this.color.length) {
        var colors = 'using colors '
        for (var item of this.color) {
          colors = colors + item + ' '
        }
        text = text + colors
      }
      if (this.font.length) {
        var fonts = ', fonts '
        for (var item of this.font) {
          fonts = fonts + item + ' '
        }
        text = text + fonts
      }
      text = text + ' --v 6.0'
      await axios
        .post('/imagine', { text })
        .then(response => response.data)
        .then(response => {
          document.getElementById('submit').disabled = false
          document.getElementById('submit').innerHTML = 'Submit'
          window.location.href = '/imagine/' + response['code'];
        }).catch(error => {
          this.error = error.response.data
          document.getElementById('submit').disabled = false
          document.getElementById('submit').innerHTML = 'Submit'
        })

    }
  }
}
</script>
